import requests
from scoreScraper import get_information
import json
from datetime import date
import ast

class SportsEvent():

    def __init__(self, league, teams, date):
        #TODO Reduce the number of required params
        self.league = league
        self.teams = teams
        self.result = None
        self.date = date

    def __eq__(self, event):
        return self.league == event.league and \
                self.teams == event.teams and \
                self.date == event.date

    def toJSON(self):
        return json.dumps(self, default=lambda o: o.__dict__,
                          sort_keys=True, indent=4)

    def update_result(self):
        info = get_information(self)

        if info['status']['type']['completed']:
            result = {}
            game_info = info['competitions'][0]
            team1_info, team2_info = game_info['competitors'][0], game_info['competitors'][1]
            result[team1_info['team']['displayName'].lower()] = {'score': team1_info['score'],
                                                                 'line_scores': team1_info['linescores'],
                                                         'team_stats': team1_info['statistics'][0]}
            result[team2_info['team']['displayName'].lower()] = {'score': team2_info['score'],
                                                                 'line_scores': team2_info['linescores'],
                                                         'team_stats': team2_info['statistics'][0]}

            self.result = result
            if team1_info['homeAway'] == 'home':
                print("Updated result for " + team1_info['team']['displayName'] + " at " + team2_info['team']['displayName'])
            else:
                print("Updated result for " + team2_info['team']['displayName'] + " at " + team1_info['team']['displayName'])

        else:
            print("Error: Event has not occured")



